document.addEventListener("DOMContentLoaded", function() {
  let btn = document.querySelector('#btn');
  let sidebar = document.querySelector('.sidebar');
  let coins_display = document.querySelector('.coins_display');
  
  btn.onclick = function () {
    sidebar.classList.toggle('active');
    coins_display.classList.toggle('active');
  };
})

function buyPack() {
  fetch('/buyPack')
    .then(response => response.json())
    .then(data => {
      coins = document.getElementById('coins_shop');
      coins.innerHTML = data.coins;
      coins_bar = document.getElementById('coins_bar');
      coins_bar.innerHTML = data.coins;

      if (data.cards != null) {
        const loot_modal = document.getElementById('loot_page');
        const loot_close = document.getElementById('loot_close');
        const loot_cards = document.getElementById('loot_cards');

        content = '';

        for (const card in data.cards) {
          card_data = data.rarity[data.cards[card]]
          card_name = data.cards[card]
          content += 
            `<div class="card_box">
              <img id="${card}" src="static/cards/${card_name}.png" class="card_${card_data['rarity']}" alt="${card_name}">
              <p class="card_name">${card_name}</p>
            </div>`;
        }

        loot_cards.innerHTML = content;

        loot_modal.inert = true;

        loot_modal.showModal();

        loot_modal.inert = false;

        loot_close.addEventListener('click', () => {
          loot_modal.close();
        });
      }

    })
}

